	@Autowired
	FollowerRepository followerRepository;

	@Autowired
	private JwtService jwtService;

	@PostMapping(path = "/register", consumes = MediaType.APPLICATION_JSON_VALUE)
	@Operation(summary = "Registro de usuarios", parameters = {
			@Parameter(name = "lang", in = ParameterIn.HEADER, allowEmptyValue = false, description = "Language", schema = @Schema(type = "string", defaultValue = "es")) }, requestBody = @io.swagger.v3.oas.annotations.parameters.RequestBody(description = "Data to send", content = @Content(mediaType = "application/json", schema = @Schema(implementation = UserToRegisterDTO.class), examples = {
					@ExampleObject(value = "{\r\n" + "\"username\": \"Parrado\",\r\n"
							+ "\"email\": \"Parrado@gmail.com\",\r\n"
							+ "\"photo\": \"\",\r\n"
							+ "\"password\": \"Parrado\"\r\n" + "}") })), responses = {
									@ApiResponse(responseCode = "200", description = "OK", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "500", description = "Server Error", content = @Content(mediaType = "application/json")) })
	public ResponseEntity register(@RequestBody UserToRegisterDTO u) {
		Token token = new Token();
		User user = new User();
		user.setAvatar(u.photo);
		user.setRegistrationDate(new Timestamp(System.currentTimeMillis()));
		user.setUsername(u.username);
		user.setEmail(u.email.toLowerCase());
		user.setPassword(encodePassword(u.password));
		try {
			token.token = jwtService.generateToken(userRepository.save(user).getId() + "");
			return new ResponseEntity<>(token, HttpStatus.OK);
		} catch (Exception e) {
			return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
		}

	}

	@GetMapping("/getProfileById/{id}")
	@Operation(summary = "Obtener el perfil de un usuario por su id", parameters = {
			@Parameter(name = "lang", in = ParameterIn.HEADER, allowEmptyValue = false, description = "Language", schema = @Schema(type = "string", defaultValue = "es")) }, requestBody = @io.swagger.v3.oas.annotations.parameters.RequestBody(description = "Data to send", content = @Content(mediaType = "application/json", schema = @Schema(implementation = OtherUserProfileDTO.class), examples = {
					@ExampleObject(value = "{\r\n" + "\"id\": \"44\",\r\n"
							+ "\"token\": \"Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiI0OCIsImlhdCI6MTcwNzcyNDgwNCwiZXhwIjoxNzM5MzQ3MjA0fQ.TMMNUfQFOkSuG5oq_YrcOX95oVUWcEurZ_ls8IZuK1M\"\r\n"
							+ "}") })), responses = {
									@ApiResponse(responseCode = "200", description = "OK", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "500", description = "Server Error", content = @Content(mediaType = "application/json")) })
	public ResponseEntity getProfileById(@PathVariable(value = "id") int id,
			@RequestParam(value = "token", required = false, defaultValue = "") String token) {
		OtherUserProfileDTO profileDTO = new OtherUserProfileDTO();
		try {
			User u = userRepository.findById(id);
			profileDTO.id = u.getId();
			profileDTO.avatar = u.getAvatar();
			profileDTO.email = u.getEmail();
			profileDTO.registration_date = u.getRegistrationDate();
			profileDTO.username = u.getUsername();
			if (!token.isBlank()) {
				if (token != null && token.startsWith("Bearer ")) {
					int userLogedId = Integer.parseInt(jwtService.extractId(token.substring(7)));
					if (u.getId() == userLogedId)
						profileDTO.autoFollow = true;
					else {
						Follower follow = followerRepository.findByUser1IdAndUser2Id(userLogedId, id);
						if (follow != null) {
							profileDTO.follow = true;
						}
					}
				}
			}
		} catch (Exception e) {
			return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
		}
		return new ResponseEntity<>(profileDTO, HttpStatus.OK);
	}

	@GetMapping("/getMyProfile")
	@Operation(summary = "Obtener el perfil de un usuario autenticado usado en sus setting", parameters = {
			@Parameter(name = "lang", in = ParameterIn.HEADER, allowEmptyValue = false, description = "Language", schema = @Schema(type = "string", defaultValue = "es")) }, requestBody = @io.swagger.v3.oas.annotations.parameters.RequestBody(description = "Data to send", content = @Content(mediaType = "application/json", schema = @Schema(implementation = MyUserProfileSettingsDTO.class), examples = {
					@ExampleObject(value = "{\r\n"
							+ "\"token\": \"Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiI0OCIsImlhdCI6MTcwNzcyNDgwNCwiZXhwIjoxNzM5MzQ3MjA0fQ.TMMNUfQFOkSuG5oq_YrcOX95oVUWcEurZ_ls8IZuK1M\"\r\n"
							+ "}") })), responses = {
									@ApiResponse(responseCode = "200", description = "OK", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "500", description = "Server Error", content = @Content(mediaType = "application/json")) })
	public ResponseEntity getProfileById(
			@RequestParam(value = "token", required = false, defaultValue = "") String token) {
		MyUserProfileSettingsDTO myProfileDTO = new MyUserProfileSettingsDTO();
		try {
			if (!token.isBlank()) {
				if (token != null && token.startsWith("Bearer ")) {
					User u = userRepository.findById(Integer.parseInt(jwtService.extractId(token.substring(7))));
					myProfileDTO.avatar = u.getAvatar();
					myProfileDTO.email = u.getEmail();
					myProfileDTO.registration_date = u.getRegistrationDate();
					myProfileDTO.username = u.getUsername();
				}
			}

		} catch (Exception e) {
			return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
		}
		return new ResponseEntity<>(myProfileDTO, HttpStatus.OK);
	}

	@DeleteMapping("/deleteAccount")
	@Operation(summary = "Borrar un usuario", parameters = {
			@Parameter(name = "lang", in = ParameterIn.HEADER, allowEmptyValue = false, description = "Language", schema = @Schema(type = "string", defaultValue = "es")) }, requestBody = @io.swagger.v3.oas.annotations.parameters.RequestBody(description = "Data to send", content = @Content(mediaType = "application/json", schema = @Schema(implementation = UserToRegisterDTO.class), examples = {
					@ExampleObject(value = "{\r\n" + "\"username\": \"Parrado\",\r\n" + "\"pass\": \"Parrado\"\r\n"
							+ "}") })), responses = {
									@ApiResponse(responseCode = "200", description = "OK", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "409", description = "Conflict ", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "500", description = "Server Error", content = @Content(mediaType = "application/json")) })
	public ResponseEntity deleteAccount(@RequestParam(value = "username") String username,
			@RequestParam(value = "pass") String pass) {
		try {
			User u = userRepository.findByUsernameAndPassword(username, encodePassword(pass));
			if (u == null)
				return new ResponseEntity<>(false, HttpStatus.CONFLICT);
			userRepository.delete(u);
		} catch (Exception e) {
			return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
		}
		return new ResponseEntity<>(null, HttpStatus.OK);
	}

	@PostMapping("/changeFollow")
	@Operation(summary = "Alternar de follow a unfollow y viceversa", parameters = {
			@Parameter(name = "lang", in = ParameterIn.HEADER, allowEmptyValue = false, description = "Language", schema = @Schema(type = "string", defaultValue = "es")) }, requestBody = @io.swagger.v3.oas.annotations.parameters.RequestBody(description = "Data to send", content = @Content(mediaType = "application/json", schema = @Schema(implementation = UserToRegisterDTO.class), examples = {
					@ExampleObject(value = "{\r\n" + "\"userId\": \"44\",\r\n"
							+ "\"token\": \"Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiI0OCIsImlhdCI6MTcwNzcyNDgwNCwiZXhwIjoxNzM5MzQ3MjA0fQ.TMMNUfQFOkSuG5oq_YrcOX95oVUWcEurZ_ls8IZuK1M\",\r\n"
							+ "\"follow\": \"false\"\r\n" + "}") })), responses = {
									@ApiResponse(responseCode = "200", description = "OK", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "500", description = "Server Error", content = @Content(mediaType = "application/json")) })
	public ResponseEntity changeFollow(@RequestBody ChangeFollowDTO c) {
		ChangeFollowDTO isFollow = new ChangeFollowDTO();
		try {
			User u = userRepository.findById(c.userId);
			if (!c.token.isBlank()) {
				if (c.token != null && c.token.startsWith("Bearer ")) {
					User userLogged = userRepository
							.findById(Integer.parseInt(jwtService.extractId(c.token.substring(7))));
					if (c.follow) {
						Follower newFollow = new Follower();
						newFollow.setUser1(userLogged);
						newFollow.setUser2(u);
						// Crear un nuevo seguimiento
						followerRepository.save(newFollow);
						isFollow.follow = true;
					} else {
						Follower UnFollow = followerRepository.findByUser1IdAndUser2Id(userLogged.getId(), u.getId());
						// Borrar seguimiento existente
						followerRepository.delete(UnFollow);
						isFollow.follow = false;
					}
				}
			}
		} catch (Exception e) {
			return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
		}
		return new ResponseEntity<>(isFollow, HttpStatus.OK);
	}

	@PostMapping("/changeProfile")
	@Operation(summary = "Cambiar foto,username y email de tu perfil", parameters = {
			@Parameter(name = "lang", in = ParameterIn.HEADER, allowEmptyValue = false, description = "Language", schema = @Schema(type = "string", defaultValue = "es")) }, requestBody = @io.swagger.v3.oas.annotations.parameters.RequestBody(description = "Data to send", content = @Content(mediaType = "application/json", schema = @Schema(implementation = UserToRegisterDTO.class), examples = {
					@ExampleObject(value = "{\r\n"
							+ "\"token\": \"Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiI0OCIsImlhdCI6MTcwNzcyNDgwNCwiZXhwIjoxNzM5MzQ3MjA0fQ.TMMNUfQFOkSuG5oq_YrcOX95oVUWcEurZ_ls8IZuK1M\",\r\n"
							+ "\"email\": \"MariadelMarChanged@gmail.com\",\r\n"
							+ "\"avatar\": \"\",\r\n"
							+ "\"username\": \"MariadelMarChanged\"\r\n" + "}") })), responses = {
									@ApiResponse(responseCode = "200", description = "OK", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "409", description = "Conflict ", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "500", description = "Server Error", content = @Content(mediaType = "application/json")) })
	public ResponseEntity changeProfile(@RequestBody ChangeUserProfile c) {
		User u = userRepository.findById(Integer.parseInt(jwtService.extractId(c.token.substring(7))));
		try {
			if ((userRepository.findByUsername(c.username) == null || u.getUsername().equals(c.username))
					&& (userRepository.findByEmail(c.email) == null || u.getEmail().equals(c.email))) {
				u.setAvatar(c.avatar);
				u.setEmail(c.email);
				u.setUsername(c.username);
				userRepository.save(u);
				return new ResponseEntity<>(true, HttpStatus.OK);
			}
		} catch (Exception e) {
			return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
		}
		return new ResponseEntity<>(false, HttpStatus.CONFLICT);
	}

	@PostMapping("/changePass")
	@Operation(summary = "Cambiar contraseña", parameters = {
			@Parameter(name = "lang", in = ParameterIn.HEADER, allowEmptyValue = false, description = "Language", schema = @Schema(type = "string", defaultValue = "es")) }, requestBody = @io.swagger.v3.oas.annotations.parameters.RequestBody(description = "Data to send", content = @Content(mediaType = "application/json", schema = @Schema(implementation = UserToRegisterDTO.class), examples = {
					@ExampleObject(value = "{\r\n"
							+ "\"token\": \"Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiI0OCIsImlhdCI6MTcwNzcyNDgwNCwiZXhwIjoxNzM5MzQ3MjA0fQ.TMMNUfQFOkSuG5oq_YrcOX95oVUWcEurZ_ls8IZuK1M\",\r\n"
							+ "\"newPass\": \"1234\",\r\n" + "\"oldPass\": \"MariadelMar\"\r\n"
							+ "}") })), responses = {
									@ApiResponse(responseCode = "200", description = "OK", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "409", description = "Conflict ", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "500", description = "Server Error", content = @Content(mediaType = "application/json")) })
	public ResponseEntity changePass(@RequestBody ChangePassDTO c) {
		User u = userRepository.findById(Integer.parseInt(jwtService.extractId(c.token.substring(7))));
		try {
			if (!verifyPassword(c.oldPass, u.getPassword()))
				return new ResponseEntity<>(false, HttpStatus.CONFLICT);
			u.setPassword(encodePassword(c.newPass));
			userRepository.save(u);
			return new ResponseEntity<>(true, HttpStatus.OK);
		} catch (Exception e) {
			return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
		}

	}

	@GetMapping("/getDetailsByToken/{tokenUrl}")
	@Operation(summary = "Obtener detalles de un usuario autenticado, usado en la navbar", parameters = {
			@Parameter(name = "lang", in = ParameterIn.HEADER, allowEmptyValue = false, description = "Language", schema = @Schema(type = "string", defaultValue = "es")) }, requestBody = @io.swagger.v3.oas.annotations.parameters.RequestBody(description = "Data to send", content = @Content(mediaType = "application/json", schema = @Schema(implementation = UserToRegisterDTO.class), examples = {
					@ExampleObject(value = "{\r\n"
							+ "\"token\": \"Bearer eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiI0OCIsImlhdCI6MTcwNzcyNDgwNCwiZXhwIjoxNzM5MzQ3MjA0fQ.TMMNUfQFOkSuG5oq_YrcOX95oVUWcEurZ_ls8IZuK1M\"\r\n"
							+ "}") })), responses = {
									@ApiResponse(responseCode = "200", description = "OK", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "500", description = "Server Error", content = @Content(mediaType = "application/json")) })
	public ResponseEntity getDetailsByToken(@PathVariable(value = "tokenUrl") String tokenUrl) {
		UserDetailsDTO details = new UserDetailsDTO();
		try {
			if (tokenUrl != null && tokenUrl.startsWith("Bearer ")) {
				String token = tokenUrl.substring(7);
				int id = Integer.parseInt(jwtService.extractId(token));
				User u = userRepository.findById(id);
				details.username = u.getUsername();
				details.email = u.getEmail();
				details.userPhoto = u.getAvatar();
			}
		} catch (Exception e) {
			return new ResponseEntity<>(null, HttpStatus.INTERNAL_SERVER_ERROR);
		}
		return new ResponseEntity<>(details, HttpStatus.OK);
	}

	@PostMapping("/logIn")
	@Operation(summary = "Logearse", parameters = {
			@Parameter(name = "lang", in = ParameterIn.HEADER, allowEmptyValue = false, description = "Language", schema = @Schema(type = "string", defaultValue = "es")) }, requestBody = @io.swagger.v3.oas.annotations.parameters.RequestBody(description = "Data to send", content = @Content(mediaType = "application/json", schema = @Schema(implementation = UserToRegisterDTO.class), examples = {
					@ExampleObject(value = "{\r\n" + "\"username\": \"MariadelMar\",\r\n" + "\"password\": \"MariadelMar\"\r\n"
							+ "}") })), responses = {
									@ApiResponse(responseCode = "200", description = "OK", content = @Content(mediaType = "application/json")),
									@ApiResponse(responseCode = "404", description = "Not found", content = @Content(mediaType = "application/json")) })
	public ResponseEntity getByUsernameAndPassword(@RequestBody UserToLogDTO user) {
		Token token = new Token();
		try {
			User u = userRepository.findByUsernameAndPassword(user.username, encodePassword(user.password));
			token.token = jwtService.generateToken("" + u.getId());
		} catch (Exception e) {
			return new ResponseEntity<>(null, HttpStatus.NOT_FOUND);
		}
		return new ResponseEntity<>(token, HttpStatus.OK);
	}

