	public static String encodePassword(String password) {
		try {
			// Usar SHA-256 para hashear la contraseña
			MessageDigest digest = MessageDigest.getInstance("SHA-256");
			byte[] hashedBytes = digest.digest(password.getBytes());

			// Convertir los bytes hasheados a una representación hexadecimal
			StringBuilder hexString = new StringBuilder();
			for (byte b : hashedBytes) {
				String hex = Integer.toHexString(0xff & b);
				if (hex.length() == 1)
					hexString.append('0');
				hexString.append(hex);
			}

			return hexString.toString();
		} catch (NoSuchAlgorithmException e) {
			e.printStackTrace();
			// Manejar la excepción de algoritmo no encontrado según sea necesario
			return null;
		}
	}

	public static boolean verifyPassword(String enteredPassword, String hashedPassword) {
		// Hashear la contraseña ingresada
		String hashedEnteredPassword = encodePassword(enteredPassword);

		// Comparar con la contraseña almacenada
		return hashedEnteredPassword != null && hashedEnteredPassword.equals(hashedPassword);
	}

